INSERT INTO `wdzx_navigation_submit` VALUES ('4', '长久贷', 'www.changjiudai.com', null, '2962559158', '', '上海长久金融信息服务有限公司中国领先的创新型互联网金融信息服务商，通过行业优秀资源的整合，专业的服务团队和雄厚的资金实力，健全的风险管控体系，为个人和中小企业提供融资渠道策划服务，帮助中小企业解决资金需求，正确引导、促进民间资本公开化、透明化，更好的为中小企业服务，为实体经济服务。2013年上海市政府已经对国家金融体系进行综合改革，市政府金融办积极争取引进国内几家P2P网络借贷公司进驻上海市借贷登记服务中心。上海长久金融信息服务有限公司全体股东一致认为未来的金融创新源泉来自网络互联网平台，组建长久贷借贷信息平台符合上海市政府一系列金融创新的文件精神，将受到国家和政府政策的鼓励和支持。', '1', '0', '', '');
