INSERT INTO `wdzx_navigation_submit` VALUES ('142', '易众网', 'www.yizhongwang.com', null, '627194123', '', '易众网是山西易众电子商务有限公司旗下的专业投融资服务平台，致力于为中小微企业、个人及金融机构提供多方共赢的新型电子商务金融模式。\r\n针对有融资或投资需求的个人、中小微型企业、融资性担保机构等客户及合作对象，易众网通过持续的资源匹配和产品开发，在严格控制风险的同时，尽可能的降低借款成本及提供专业、高效的融资服务；通过减少投融资中间环节以及与优质融资性担保公司的合作，让大众投资者获得比银行理财产品更高且稳健的理财收益。\r\n', '1', '1411348921', '山西', '2014年9月12日');
